using UnityEngine; using ProjectChimera.Core; using ProjectChimera.Data.Economy; namespace ProjectChimera.Systems.Facilities { public class ConstructionEconomyManager : ChimeraManager { [Header("Construction Economics")] public float BaseConstructionCost = 1000f; public float LaborCostMultiplier = 1.2f; public float MaterialCostMultiplier = 1.5f; protected override void OnManagerInitialize() { Debug.Log("ConstructionEconomyManager initialized"); } protected override void OnManagerShutdown() { Debug.Log("ConstructionEconomyManager shutdown"); } public float CalculateConstructionCost(string facilityType, Vector3 size) { float baseCost = BaseConstructionCost * (size.x * size.y * size.z); float laborCost = baseCost * LaborCostMultiplier; float materialCost = baseCost * MaterialCostMultiplier; return baseCost + laborCost + materialCost; } } }
